import React from 'react'
import createStore from './store'


const isServer = typeof window === 'undefined'
const __NEXT_REDUX_STORE__ = '__NEXT_REDUX_STORE__'

const getOrCreateStore = (initialState) => {
  if (isServer) {
    return createStore(initialState)
  }

  if (!window[__NEXT_REDUX_STORE__]) {
    window[__NEXT_REDUX_STORE__] = createStore(initialState)
  }

  return window[__NEXT_REDUX_STORE__]
}
export default MyApp => {

  class WithReduxApp extends React.Component {
    constructor (props) {
      super(props)
      this.reduxStore = getOrCreateStore(props.initialReduxState)
    }

    render () {
      const { Component, pageProps, ...rest } = this.props
      if (pageProps) {
        pageProps.test = '123'
      }

      return <MyApp Component={Component} pageProps={pageProps} {...rest} reduxStore={this.reduxStore} />
    }
  }



  WithReduxApp.getInitialProps = async (ctx) => {
    console.log('WithReduxApp getInitialProps::::')
    const reduxStore = getOrCreateStore()
    ctx.reduxStore = reduxStore
    let appProps = {}
    if (typeof MyApp.getInitialProps === 'function') {
      appProps = await MyApp.getInitialProps(ctx)
    }

    return {
      ...appProps,
      initialReduxState: reduxStore.getState()
    }
  }

  return WithReduxApp
}
